static uint32_t animation_frame_timer;
static uint8_t  current_frame = -1;

void fullscreen_animation(void) {
    if (animation_frame_timer && timer_elapsed32(animation_frame_timer) < (1000 / 3)) {
        return;
    }

    current_frame         = (current_frame + 1) % 5;
    animation_frame_timer = timer_read32();

    oled_set_cursor(0, 0);
    oled_write_raw_P(animation_frames[current_frame], sizeof(animation_frame_1));
}
